import React, { useState } from 'react';

export default function DarkLightToggle() {

    /*
       theme = holds current state
       toggleTheme = function to change current state
       useState(false) = passes in initial state for theme of 'dark'
    */
    const [theme, toggleTheme] = useState('dark');

    // toggler gets called on button click, and simply switches the theme
    const toggler = () => {
        if (theme == 'light') {
            toggleTheme('dark');
        } else {
            toggleTheme('light');
        }
    };

    // useEffect hook to adjust coloring 
    useEffect(() => {
        console.log("body" + theme)
        document.body.className = "body" + theme;
        

    }, [theme]);

    return (
        <div className={`{$theme}Theme`}>
            <button onClick={toggler}>themeToggler</button>
        </div>
    )
}
